
-- CREATE AND ALTER TABLE

CREATE TABLE IF NOT EXISTS user_findme (
    id SERIAL,
    email VARCHAR(50) NOT NULL,
    "password" VARCHAR(255) NOT NULL,
    created_at DATE DEFAULT '2021-04-12' NOT NULL,
    updated_at DATE DEFAULT NULL NULL,
    locale VARCHAR(50) NOT NULL,
    "enable" BOOLEAN NOT NULL,
    "level" VARCHAR(50) NOT NULL,
    total_distance FLOAT NOT NULL,
    picture TEXT NOT NULL,
    pseudo VARCHAR(15) NOT NULL,
    CONSTRAINT PK_User_findme_Id PRIMARY KEY (id)
);

CREATE TABLE IF NOT EXISTS category (
    id SERIAL,
    "name" VARCHAR(255) NOT NULL,
    CONSTRAINT PK_Category_Id PRIMARY KEY (id)
);

CREATE TABLE IF NOT EXISTS place (
    id SERIAL,
    "name" VARCHAR(50) NOT NULL,
    latitude FLOAT NOT NULL,
    longitude FLOAT NOT NULL,
    difficulty INT NOT NULL,
    radius_type VARCHAR(10) NOT NULL,
    category_id INT NOT NULL,
    CONSTRAINT PK_Place_Id PRIMARY KEY (id),
    CONSTRAINT fk_place_category_id_id FOREIGN KEY (category_id) REFERENCES category(id) ON DELETE RESTRICT ON UPDATE RESTRICT
);

CREATE TABLE IF NOT EXISTS user_itinerary (
    user_id INT,
    place_id INT,
    CONSTRAINT PK_User_itinerary PRIMARY KEY (user_id, place_id),
    CONSTRAINT fk_user_itinerary_user_id_id FOREIGN KEY (user_id) REFERENCES user_findme(id) ON DELETE RESTRICT ON UPDATE RESTRICT,
    CONSTRAINT fk_user_itinerary_place_id_id FOREIGN KEY (place_id) REFERENCES place(id) ON DELETE RESTRICT ON UPDATE RESTRICT
);

CREATE TABLE IF NOT EXISTS itinerary (
    id SERIAL,
    "name" VARCHAR(255) NOT NULL,
    duration FLOAT NOT NULL,
    "length" FLOAT NOT NULL,
    active BOOLEAN NOT NULL,
    CONSTRAINT PK_Itinerary_Id PRIMARY KEY (id)
);

CREATE TABLE IF NOT EXISTS itinerary_place (
    itinerary_id INT,
    place_id INT,
    CONSTRAINT PK_Itinerary_place PRIMARY KEY (itinerary_id, place_id),
    CONSTRAINT fk_itinerary_place_itinerary_id_id FOREIGN KEY (itinerary_id) REFERENCES itinerary(id) ON DELETE RESTRICT ON UPDATE RESTRICT,
    CONSTRAINT fk_itinerary_place_place_id_id FOREIGN KEY (place_id) REFERENCES place(id) ON DELETE RESTRICT ON UPDATE RESTRICT
);

ALTER TABLE user_findme ADD CONSTRAINT user_findme_id_unique UNIQUE (id);
ALTER TABLE category ADD CONSTRAINT category_id_unique UNIQUE (id);
ALTER TABLE place ADD CONSTRAINT place_id_unique UNIQUE (id);
ALTER TABLE itinerary ADD CONSTRAINT itinerary_id_unique UNIQUE (id);

